import React from 'react';

function Card() {
    {
        var countDownDate = new Date("Oct 14, 2019 0:0:0").getTime();
        var displayDate = document.querySelector(".date-display")
        var date = new Date();
        var span = document.getElementById('span');
        // Update the count down every 1 second
        var x = setInterval(function() {

        // Get today's date and time
        var now = new Date().getTime();

        // Find the distance between now and the count down date
        var distance = countDownDate - now;

        // Time calculations for days, hours, minutes and seconds
        var days = Math.floor(distance / (1000 * 60 * 60 * 24));
        var hours = Math.floor((distance % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60));
        var minutes = Math.floor((distance % (1000 * 60 * 60)) / (1000 * 60));
        var seconds = Math.floor((distance % (1000 * 60)) / 1000);

        // Display the result in the element with id="demo"
        span.textContent = days + "d " + hours + "h "
        + minutes + "m " + seconds + "s ";

        // If the count down is finished, write some text 
        if (distance < 0) {
            clearInterval(x);
            span.textContent = "TODAY IS THE DAY!!!";
        }
        }, 1000);
    }
    return (

            <div className="Card-Holder">

                <div className="row align-items-center">
                    <div className="col"> </div>
                    <div className="col-5"id="Cabin-Countdown">
                        <h1>Cabin Countdown!</h1>
                        <h1 id="span">15:00:00 </h1>
                    </div>
                    <div className="col"> </div>
                </div>
            </div>
    )
}

export default Card;
